import machine
import kooka
import math

big_R = None
x = None
little_r = None
y = None
pixel_r = None
my_1_k = None
k = None
t = None
lk = None
_1_k__k = None

Number = (bool, int, float)


big_R = 35
little_r = 16
pixel_r = 10
k = little_r / big_R
lk = pixel_r / big_R
my_1_k = 1 - k
_1_k__k = my_1_k / k
t = 123
kooka.display.fill(0)

kooka.display.show()

while True:
    x = my_1_k * math.cos(t / 180.0 * math.pi) + lk * math.cos((_1_k__k * t) / 180.0 * math.pi)
    y = my_1_k * math.sin(t / 180.0 * math.pi) + -lk * math.sin((_1_k__k * t) / 180.0 * math.pi)
    kooka.display.pixel(int(63 + big_R * x), int(32 + big_R * y), int(1))
    t = (t if isinstance(t, Number) else 0) + 1
    kooka.display.show()
    if kooka.button_a.was_pressed():
        raise SystemExit
    kooka.display.show()
    machine.idle()

