import machine, kooka
import fonts
import time
import random
import math
import framebuf

x = None
y = None
vert_i = None
vert_j = None
Score = None
horiz_i = None
horiz_j = None
xd = None
yd = None
collision = None

Number = (bool, int, float)

# Describe this function...
def sprite(x, y):
    global vert_i, vert_j, Score, horiz_i, horiz_j, xd, yd, collision
    if x % 10 < 5:
        kooka.display.blit(framebuf.FrameBuffer(bytearray(b"\x18\x3c\xe7\xc3\xff\xff\x66\xc3"), 8, 8, framebuf.MONO_HMSB), int(x), int(y), 0)
    else:
        kooka.display.blit(framebuf.FrameBuffer(bytearray(b"\x18\x3c\xe7\xc3\xff\xff\x66\x3c"), 8, 8, framebuf.MONO_HMSB), int(x), int(y), 0)

# Describe this function...
def reset():
    global x, y, vert_i, vert_j, Score, horiz_i, horiz_j, xd, yd, collision
    vert_i = random.randint(0, 120)
    vert_j = 0
    horiz_i = 0
    horiz_j = random.randint(10, 50)



kooka.display.show()

# Initialise timer counters.
_timer20 = time.ticks_ms()

# On-start code, run once at start-up.
if True:
    kooka.display.clear()
    kooka.display.setfont(fonts.mono6x7)
    kooka.display.print('Sprites', show=0)
    kooka.display.print('avoid or collide', show=0)
    kooka.display.print('C & D to steer', show=0)
    kooka.display.print('Button A to exit', show=0)
    kooka.display.show()
    time.sleep(3)
    reset()
    Score = 0

# Main loop code, run continuously.
while True:
    if time.ticks_diff(time.ticks_ms(), _timer20) >= 0:
        _timer20 += 20
        kooka.display.clear()
        kooka.display.text(str(Score), int(110), int(10), int(1))
        sprite(horiz_i, horiz_j)
        horiz_i = (horiz_i if isinstance(horiz_i, Number) else 0) + 1
        sprite(vert_i, vert_j)
        vert_j = (vert_j if isinstance(vert_j, Number) else 0) + 1
        if horiz_i > 127:
            horiz_i = 0
            horiz_j = (horiz_j if isinstance(horiz_j, Number) else 0) + 8
            if horiz_j > 50:
                horiz_j = random.randint(10, 50)
        if vert_j > 70:
            vert_j = 0
            vert_i = (vert_i if isinstance(vert_i, Number) else 0) + 8
            if vert_i > 120:
                vert_i = random.randint(0, 120)
        xd = math.fabs((horiz_i + 4) - (vert_i + 4))
        yd = math.fabs((horiz_j + 4) - (vert_j + 4))
        collision = xd <= 4 and yd <= 4
        if collision:
            kooka.display.clear()
            Score = (Score if isinstance(Score, Number) else 0) + 1
            kooka.display.text(str(Score), int(110), int(10), int(1))
            kooka.display.blit(framebuf.FrameBuffer(bytearray(b"\xd1\x08\xa6\x7a\xad\xba\x14\xa9"), 8, 8, framebuf.MONO_HMSB), int(horiz_i), int(horiz_j), 0)
            kooka.display.show()
            time.sleep(1)
            reset()
    if kooka.button_a.was_pressed():
        raise SystemExit
    if kooka.button_c.is_pressed():
        vert_i = (vert_i if isinstance(vert_i, Number) else 0) + -1
    if kooka.button_d.is_pressed():
        vert_i = (vert_i if isinstance(vert_i, Number) else 0) + 1
    kooka.display.show()
    machine.idle()

